<img class="photo" data-id="{{{@$file->id}}}" data-x1="{{{@$file->x1}}}" data-x2="{{{@$file->x2}}}" data-y1="{{{@$file->y1}}}" data-y2="{{{@$file->y2}}}"  data-size='{{{@$file->size}}}' data-name='{{isset($file) ? basename(@$file->path) : ""}}' data-filefull='{{isset($file)?asset($file->path) : ""}}' src='{{isset($file)?Croppa::url(@"/".$file->path, $file->width, $file->height,array("trim"=>array($file->x1,$file->y1,$file->x2,$file->y2),"resize")) : ""}}' data-ratio='{{$ratio}}' data-type='picture-resizer'  data-url='{{action("ImageController@handleUpload")}}'  />